def calculatrice(a, b, operateur):
    try:
        if operateur == '+':
            return a + b
        elif operateur == '-':
            return a - b
        elif operateur == '*':
            return a * b
        elif operateur == '/':
            if b == 0:
                return "Division par zéro!"
            else:
                return a / b
        else:
            return "Opérateur invalide"
    except TypeError:
        return "Type d'argument invalide"
