import string

def compter_mots(chaine):
    chaine = chaine.lower().translate(str.maketrans('', '', string.punctuation))
    mots = chaine.split()
    compteur = {}
    for mot in mots:
        if mot in compteur:
            compteur[mot] += 1
        else:
            compteur[mot] = 1
    return compteur
