// Solution for Swap two integers using pointers
#include <stdio.h>

void swap(int *a, int *b) {
    int temp = *a;
    *a = *b;
    *b = temp;
}

int main() {
    int num1 = 10;
    int num2 = 20;

    printf("Before swap: num1 = %d, num2 = %d\n", num1, num2);

    swap(&num1, &num2);

    printf("After swap: num1 = %d, num2 = %d\n", num1, num2);

    return 0;
}

// Solution for Find the length of a string using pointers
#include <stdio.h>

int stringLength(const char *str) {
    int length = 0;
    while (*str != '\0') {
        length++;
        str++;
    }
    return length;
}

int main() {
    char myString[] = "Hello, C!";
    int len = stringLength(myString);
    printf("The length of the string '%s' is: %d\n", myString, len);
    return 0;
}

// Solution for Sum of array elements using pointers
#include <stdio.h>

int main() {
    int arr[] = {1, 2, 3, 4, 5};
    int n = sizeof(arr) / sizeof(arr[0]);
    int sum = 0;
    int *ptr = arr;

    printf("Array elements: ");
    for (int i = 0; i < n; i++) {
        printf("%d ", *ptr);
        sum += *ptr;
        ptr++;
    }
    printf("\n");

    printf("Sum of array elements: %d\n", sum);

    return 0;
}
